package bbConvert

import "testing"

func TestBasicFormatting(t *testing.T) {
	testStr := "[b]Some Text[/b][i]Some Text[/i][u]Some Text[/u][s]some Text[/s][size=20pt]Some Text[/size][font color=000000]Some Text[/font][font color=#000000]Some Text[/font][font variant=lower]Some Text[/font][font variant=smallcaps]Some Text[/font][color=red]Some Text[/color][color=#000000]Some Text[/color][color=000000]Some Text[/color][smallcaps]Some Text[/smallcaps][url]Link address[/url][url=address]Some Text[/url][url title=\"Title\"]Link address[/url][title]Some Text[/title][t1]Some Text[/t1][t2]Some Text[/t2][t3]Some Text[/t3][t4]Some Text[/t4][t5]Some Text[/t5][t6]Some Text[/t6][align=center]Some Text[/align]"
	expected := "<p class=\"namey facey bloop\"><b>Some Text</b><i>Some Text</i><u>Some Text</u><s>some Text</s><span style='font-color:20pt;'>Some Text</span><span style=\"color:#000000;\">Some Text</span><span style=\"color:#000000;\">Some Text</span><span style=\"text-transform:lowercase;\">Some Text</span><span style=\"font-variant:small-caps;\">Some Text</span><span style='color:red;'>Some Text</span><span style='color:#000000;'>Some Text</span><span style='color:#000000;'>Some Text</span><span style=\"font-variant:small-caps;\">Some Text</span><a href=\"Link address\">Link address</a><a href=\"address\">Some Text</a><a title='Title' href=\"Link address\">Link address</a></p><h1>Some Text</h1><p class=\"namey facey bloop\"><h1>Some Text</h1><h2>Some Text</h2><h3>Some Text</h3><h4>Some Text</h4><h5>Some Text</h5><h6>Some Text</h6></p><div style=\"text-align:center;\"><p class=\"namey facey bloop\">Some Text</p></div><p class=\"namey facey bloop\"></p>"
	var conv HTMLConverter
	conv.ImplementDefaults()
	conv.AddClass("namey facey")
	conv.AddClass("bloop")
	conv.SetStyle("text-color", "red")
	conv.SetStyle("text-color", "")
	out := conv.Convert(testStr)
	if expected != out {
		t.Fatal("Got: " + out + ", Wanted: " + expected)
	}
}

