func TestMulti(t *testing.T) {
	testStr := "[font=Verdana size=20 color=red variant=upper]Some Text[/font]"
	expectedarr := append([]string{}, "<p><span style=\"color:red;font-family:Verdana;font-size:20;text-transform:uppercase;\">Some Text</span></p>", "<p><span style=\"font-size:20;text-transform:uppercase;color:red;font-family:Verdana;\">Some Text</span></p>", "<p><span style=\"text-transform:uppercase;color:red;font-family:Verdana;font-size:20;\">Some Text</span></p>", "<p><span style=\"font-family:Verdana;font-size:20;text-transform:uppercase;color:red;\">Some Text</span></p>")
	var conv HTMLConverter
	conv.ImplementDefaults()
	out := conv.Convert(testStr)
	for _, v := range expectedarr {
		if v == out {
			return
		}
	}
	t.Fatal("Got:", out)
}
